iness.</li>\r\n</ol>', 'What We Do', '', 'inherit', 'closed', 'closed', '', '417-revision-v1', '', '', '2014-06-23 12:04:38', '2014-06-23 05:04:38', '', 417, 'http://localhost/perbanas/en/417-revision-v1/', 0, 'revision', '', 0),
